mapper.select:
SELECT a.asset_detail_id assetDetailId, b.type_name typeName, c.type_detail_name typeDetailName, a.asset_number assetNumber, a.asset_name assetName, a.price price, a.acreage acreage, d.item_value priceTypeName, e.item_value acquireWayName, a.finance_accounts_date financeAccountsDate, a.tab_date tabDate, a.guarantee_date guaranteeDate, f.department_name departmentName, g.realname realname, a.purpose purpose, a.remark remark, a.specification_model specificationModel, a.brand brand, a.voucher_number voucherNumber, a.purchase_organize purchaseOrganize, a.inventory inventory, a.bar_code barCode FROM asset_detail detail LEFT JOIN asset_info a ON a.asset_id = detail.asset_id LEFT JOIN asset_type_info b ON a.type_id = b.type_id LEFT JOIN asset_type_detail c ON a.type_detail_id = c.type_detail_id LEFT JOIN dictionary_detail d ON a.price_type = d.dictionary_detail_id LEFT JOIN dictionary_detail e ON a.acquire_way = e.dictionary_detail_id LEFT JOIN department_info f ON a.manage_department = f.department_id LEFT JOIN user_info g ON a.manage_user = g.user_id WHERE a.delete_flag = 0 AND a.asset_type = #{assetType, jdbcType=INTEGER}
AND a.type_id = #{typeId, jdbcType=INTEGER}
AND a.type_detail_id = #{typeDetailId, jdbcType=INTEGER}
AND a.asset_name like CONCAT('%',#{assetName, jdbcType=VARCHAR},'%')
LIMIT #{beginIndex}, #{pageRecorders}

SELECT count(1) FROM asset_detail a, asset_info b WHERE b.delete_flag = 0 AND b.asset_type = #{assetType, jdbcType=INTEGER}
AND b.type_id = #{typeId, jdbcType=INTEGER}
AND b.type_detail_id = #{typeDetailId, jdbcType=INTEGER}
AND b.asset_name like CONCAT('%',#{assetName, jdbcType=VARCHAR},'%')

SELECT
FROM asset_info WHERE delete_flag = 0 AND status = 1
AND bar_code = #{barCode, jdbcType=VARCHAR}
AND asset_type = #{assetType, jdbcType=INTEGER}
