mapper.select:
SELECT department_id departmentId, department_name departmentName, short_name shortName, remark remark, status status FROM department_info WHERE delete_flag = 0
AND status = #{status, jdbcType=INTEGER}
AND department_name like CONCAT('%',#{departmentName, jdbcType=VARCHAR},'%')
limit #{beginIndex}, #{pageRecorders}

SELECT IFNULL( count(1), 0 ) FROM department_info WHERE delete_flag = 0
AND status = #{status, jdbcType=INTEGER}
AND department_name like CONCAT('%',#{departmentName, jdbcType=VARCHAR},'%')
