mapper.select:
SELECT a.hospital_appoint_id hospitalAppointId, a.appoint_date appointDate, b.realname realname, a.appoint_number appointNumber, a.appoint_desc appointDesc, a.contacts contacts, a.contact_way contactWay, a.appoint_status appointStatus, a.remark remark FROM hospital_appoint_info a, user_info b WHERE a.delete_flag = 0 AND a.user_id = b.user_id
AND a.appoint_date like CONCAT(#{appointDate, jdbcType=VARCHAR}, '%')
AND a.user_id = #{userId, jdbcType=VARCHAR}
AND a.appoint_number like CONCAT('%', #{appointNumber, jdbcType=VARCHAR}, '%')
limit #{beginIndex}, #{pageRecorders}
