mapper.select:
SELECT a.treatment_case_id treatmentCaseId, a.operator_date operatorDate, a.place place, a.clinical clinical, a.first_visit firstVisit, a.advice advice, a.title_tag titleTag, a.remark remark, c.horse_name horseId, b.horse_type horseType, b.horse_name horseName FROM treatment_case_info a INNER JOIN treatment_info b ON a.treatment_id = b.treatment_id LEFT JOIN horse_info c ON c.horse_id = b.horse_id WHERE a.delete_flag = 0
AND b.horse_id = #{horseId, jdbcType=VARCHAR}
AND a.title_tag like CONCAT('%',#{titleTag, jdbcType=VARCHAR},'%')
LIMIT #{beginIndex}, #{pageRecorders}

SELECT count(1) FROM treatment_case_info a INNER JOIN treatment_info b ON a.treatment_id = b.treatment_id LEFT JOIN horse_info c ON c.horse_id = b.horse_id WHERE a.delete_flag = 0
AND b.horse_id = #{horseId, jdbcType=VARCHAR}
AND a.title_tag like CONCAT('%',#{titleTag, jdbcType=VARCHAR},'%')
