mapper.select:
SELECT a.treatment_id treatmentId, a.outpatient_type outpatientType, b.appoint_number appointNumber, a.horse_type horseType, a.horse_name horseName, d.horse_name horseId, a.begin_date beginDate, a.begin_time beginTime, a.treat_name treatName, a.treat_desc treatDesc, c.name consultingRoomName, e.treatment_case_id treatmentCaseId FROM treatment_info a LEFT JOIN hospital_appoint_info b ON a.hospital_appoint_id = b.hospital_appoint_id LEFT JOIN consulting_room_info c ON a.consulting_room_id = c.consulting_room_id LEFT JOIN horse_info d ON a.horse_id = d.horse_id LEFT JOIN treatment_case_info e ON e.treatment_id = a.treatment_id WHERE a.status = 1 AND a.delete_flag = 0
AND a.horse_id = #{horseId, jdbcType=VARCHAR}
AND a.treat_name like CONCAT('%',#{treatName, jdbcType=VARCHAR},'%')
LIMIT #{beginIndex}, #{pageRecorders}

SELECT count(1) FROM treatment_info a WHERE a.status = 1 AND a.delete_flag = 0
AND a.horse_id = #{horseId, jdbcType=VARCHAR}
AND a.treat_name like CONCAT('%',#{treatName, jdbcType=VARCHAR},'%')
